    public String getText() {
        return textFld.getText();
    }
    public void setFontSizeList(int[] list) {
        sizeCb.removeAllItems();
        for (int i=0; i<list.length; i++) {
            sizeCb.addItem(list[i]);
        }
    }

    public void setFontSize(int size) {
        for(int i=0; i<sizeCb.getItemCount(); i++) {
            if ((Integer)sizeCb.getItemAt(i) == size)
                sizeCb.setSelectedIndex(i);
        }
    }

    public int getFontSize() {
        return (Integer)sizeCb.getSelectedItem();
    }
